CREATE TABLE "users" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(20) NOT NULL,
  "last_name" varchar(30) NOT NULL,
  "email" varchar(60) NOT NULL,
  "password" varchar(50) NOT NULL,
  "age" int NOT NULL,
  "role_fk" int NOT NULL
);

CREATE TABLE "courses" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title_fk" int NOT NULL,
  "description" varchar(50) NOT NULL,
  "level_fk" int NOT NULL,
  "teacher_fk" int NOT NULL,
  "categorie_fk" int NOT NULL,
  "course_url_fk" int NOT NULL
);

CREATE TABLE "courses_level" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "level" varchar(25) NOT NULL
);

CREATE TABLE "courses_teacher" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "teacher" varchar(50) NOT NULL
);

CREATE TABLE "categories" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(50) NOT NULL
);

CREATE TABLE "roles" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "name" varchar(25) NOT NULL
);

CREATE TABLE "course_title" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "title" varchar(70) NOT NULL
);

CREATE TABLE "course_url" (
  "id" INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
  "url" varchar(200) NOT NULL
);

ALTER TABLE "courses" ADD FOREIGN KEY ("teacher_fk") REFERENCES "courses_teacher" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("level_fk") REFERENCES "courses_level" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("categorie_fk") REFERENCES "categories" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("course_url_fk") REFERENCES "course_url" ("id");

ALTER TABLE "courses" ADD FOREIGN KEY ("title_fk") REFERENCES "course_title" ("id");

ALTER TABLE "users" ADD FOREIGN KEY ("role_fk") REFERENCES "roles" ("id");

